mapper.select:
select
order_item_id, order_num, product_id, price, quantity, user_id, spec_id
from fruit.order_item


        and order_item_id = #{orderItemId}


        and order_num = #{orderNum}


        and product_id = #{productId}


        and price = #{price}


        and quantity = #{quantity}


        and user_id = #{userId}


        and spec_id = #{specId}
